pluginOptions.query:
{
  site {
    siteMetadata {
      site_url: url
      title
      description: subtitle
    }
  }
}

feeds.query:
{
  allMarkdownRemark(
    limit: 1000,
    sort: { order: DESC, fields: [frontmatter___date] },
    filter: { frontmatter: { layout: { eq: "post" }, draft: { ne: true } } }
  ) {
    edges {
      node {
        html
        fields {
          slug
        }
        frontmatter {
          title
          date
          layout
          draft
          description
        }
      }
    }
  }
}

pluginOptions.query:
  {
    site {
      siteMetadata {
        url
      }
    }
    allSitePage(
      filter: {
        path: { regex: "/^(?!/404/|/404.html|/dev-404-page/)/" }
      }
    ) {
      edges {
        node {
          path
        }
      }
    }
}

